mapper.update:
		BEGIN
			UPDATE ITEM
			SET PUB = 1
			WHERE 

	        	#{id}

			;
    		UPDATE ITEM
			SET PUB = 0
			WHERE 

	        	#{id}

			;
    	END;

BEGIN
	UPDATE ITEM
	SET SUM_SCORE = #{new_sum_score}
	WHERE ID = #{id}
	;
	UPDATE ITEM
	SET NUM_SCORE = #{new_num_score}
	WHERE ID = #{id}
	;
END;

mapper.select:
SELECT SUM_SCORE
FROM ITEM
WHERE ID = #{id}

SELECT NUM_SCORE
FROM ITEM
WHERE ID = #{id}
